import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

const Step2 = () => {
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [vehicles, setVehicles] = useState([]);

  useEffect(() => {
    const fetchVehicles = async () => {
      try {
        const step2Data = JSON.parse(localStorage.getItem('step2Data'));
        const token = localStorage.getItem('token');

        const response = await axios.get(`https://calm-retreat-90846-cd036e8a822e.herokuapp.com/https://api.rentsyst.com/v2/booking/search`, {
          params: {
            date_from: step2Data.pickupDateTime,
            date_to: step2Data.returnDateTime,
            pickup_location: step2Data.selectedLocation,
            return_location: step2Data.selectedLocation
          },
          headers: {
            Authorization: `Bearer ${token}`
          }
        });

        setVehicles(response.data);
      } catch (error) {
        console.error('Error fetching vehicles:', error);
        setError('Failed to fetch vehicles');
      } finally {
        setLoading(false);
      }
    };

    fetchVehicles();
  }, []);

  return (
    <div className="step2">
      <h2>Available Vehicles</h2>
      {loading && <p>Loading...</p>}
      {error && <p>{error}</p>}
      {vehicles && vehicles.map(vehicle => (
        <div key={vehicle.id} className="vehicle-item">
          <h3>{vehicle.name}</h3>
          <p>{vehicle.description}</p>
          {/* Render other vehicle details */}
        </div>
      ))}
      {!loading && !error && (!vehicles || vehicles.length === 0) && <p>No vehicles available</p>}
    </div>
  );
};

export default Step2;
