import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

const Step2 = () => {
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [vehicles, setVehicles] = useState([]);

  useEffect(() => {
    const fetchVehicles = async () => {
      try {
        const step2Data = JSON.parse(localStorage.getItem('step2Data'));
        const token = localStorage.getItem('token');

        const response = await axios.get(`https://calm-retreat-90846-cd036e8a822e.herokuapp.com/https://api.rentsyst.com/v2/booking/search`, {
          params: {
            date_from: step2Data.pickupDateTime,
            date_to: step2Data.returnDateTime,
            pickup_location: step2Data.selectedLocation,
            return_location: step2Data.selectedLocation
          },
          headers: {
            Authorization: `Bearer ${token}`
          }
        });

        console.log('Response data:', response.data); // Add this line

        // Assuming the response structure is similar to the provided example
        const categorizedVehicles = response.data.map(vehicle => ({
          id: vehicle.id,
          name: vehicle.name,
          description: vehicle.description,
          thumbnailUrl: vehicle.thumbnail.url,
          dailyPrice: vehicle.dailyPrice,
          features: vehicle.features.map(feature => feature.name),
          // Add more categories as needed
        }));

        setVehicles(categorizedVehicles);
      } catch (error) {
        console.error('Error fetching vehicles:', error);
        setError('Failed to fetch vehicles');
      } finally {
        setLoading(false);
      }
    };

    fetchVehicles();
  }, []);

  return (
    <div className="step2">
      <h2>Available Vehicles</h2>
      {loading && <p>Loading...</p>}
      {error && <p>{error}</p>}
      {vehicles && vehicles.length > 0 && (
        <div className="vehicle-list">
          {vehicles.map(vehicle => (
            <div key={vehicle.id} className="vehicle-item">
              <div className="vehicle-thumbnail">
                <img src={vehicle.thumbnailUrl} alt={vehicle.name} />
              </div>
              <div className="vehicle-details">
                <h3>{vehicle.name}</h3>
                <p>{vehicle.description}</p>
                <p>Daily Price: {vehicle.dailyPrice}</p>
                <h4>Features:</h4>
                <ul>
                  {vehicle.features.map((feature, index) => (
                    <li key={index}>{feature}</li>
                  ))}
                </ul>
                {/* Add more vehicle details as needed */}
              </div>
            </div>
          ))}
        </div>
      )}
      {!loading && !error && (!vehicles || vehicles.length === 0) && <p>No vehicles available</p>}
    </div>
  );
};

export default Step2;
