import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

const Step2 = () => {
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [vehicles, setVehicles] = useState([]);
  const [filteredVehicles, setFilteredVehicles] = useState([]);
  const [filterOptions, setFilterOptions] = useState({
    minPrice: 0,
    maxPrice: 1000,
    minSeats: 1,
    maxSeats: 10,
  });

  useEffect(() => {
    const fetchVehicles = async () => {
      try {
        const step2Data = JSON.parse(localStorage.getItem('step2Data'));
        const token = localStorage.getItem('token');
        const response = await axios.get(`https://calm-retreat-90846-cd036e8a822e.herokuapp.com/https://api.rentsyst.com/v2/booking/search`, {
          params: {
            date_from: step2Data.pickupDateTime,
            date_to: step2Data.returnDateTime,
            pickup_location: step2Data.selectedLocation,
            return_location: step2Data.selectedLocation,
          },
          headers: {
            Authorization: `Bearer ${token}`
          }
        });

        setVehicles(response.data.data);
        setFilteredVehicles(response.data.data); // Initialize filteredVehicles with all vehicles
      } catch (error) {
        console.error('Error fetching vehicles:', error);
        setError('Failed to fetch vehicles');
      } finally {
        setLoading(false);
      }
    };

    fetchVehicles();
  }, []);

  const handleFilterChange = (event) => {
    const { name, value } = event.target;
    setFilterOptions({ ...filterOptions, [name]: value });
  };

  useEffect(() => {
    const filtered = vehicles.filter(vehicle => {
      return (
        vehicle.price_per_day >= filterOptions.minPrice &&
        vehicle.price_per_day <= filterOptions.maxPrice &&
        vehicle.seats >= filterOptions.minSeats &&
        vehicle.seats <= filterOptions.maxSeats
      );
    });
    setFilteredVehicles(filtered);
  }, [filterOptions, vehicles]);

  const calculateTripPrice = (vehicle) => {
    const { pickupDateTime, returnDateTime } = JSON.parse(localStorage.getItem('step2Data'));
    const pickupDate = new Date(pickupDateTime);
    const returnDate = new Date(returnDateTime);
    const days = Math.ceil((returnDate - pickupDate) / (1000 * 60 * 60 * 24));
    return days * vehicle.price_per_day;
  };

  return (
    <div className="step2">
      <h2>Available Vehicles</h2>
      <div className="vehicle-catalog">
        {loading && <p>Loading...</p>}
        {error && <p>{error}</p>}
        {!loading && filteredVehicles && filteredVehicles.length === 0 && <p>No vehicles available with selected filters</p>}
        {!loading && filteredVehicles && filteredVehicles.length > 0 && filteredVehicles.map(vehicle => (
          <div key={vehicle.id} className="vehicle-item">
            <h3>{vehicle.name}</h3>
            <p>Description: {vehicle.description}</p>
            <p>Features: {vehicle.features.join(', ')}</p>
            <p>Seats: {vehicle.seats}</p>
            <p>Daily Price: ${vehicle.price_per_day}</p>
            <p>Trip Price: ${calculateTripPrice(vehicle)}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Step2;
