import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

const Step2 = () => {
  const defaultFilters = {
    tripPrice: { min: '', max: '' },
    vehicleType: 'All',
    fuelType: 'All',
    transmission: 'All'
  };

  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [vehicles, setVehicles] = useState([]);
  const [filter, setFilter] = useState('');
  const [filters, setFilters] = useState(defaultFilters);

  useEffect(() => {
    const fetchVehicles = async () => {
      try {
        const step2Data = JSON.parse(localStorage.getItem('step2Data'));
        const token = localStorage.getItem('token');

        // Extracting parameters from step2Data
        const { pickupDateTime, returnDateTime, selectedLocation } = step2Data;

        // Format date_from and date_to
        const fromDate = encodeURIComponent(pickupDateTime);
        const toDate = encodeURIComponent(returnDateTime);

        // Constructing URL with parameters
        const url = `https://calm-retreat-90846-cd036e8a822e.herokuapp.com/https://api.rentsyst.com/v2/booking/search?date_from=${fromDate}&date_to=${toDate}&pickup_location=${selectedLocation}&return_location=${selectedLocation}`;

        const response = await axios.get(url, {
          headers: {
            Authorization: `Bearer ${token}`
          }
        });

        console.log('Response data:', response.data);

        // Check if response data and search exist
        if (!response.data || !response.data.vehicles) {
          throw new Error('Invalid response data format');
        }

        // Parse the vehicles from the response
        const parsedVehicles = response.data.vehicles.map(vehicle => {
          const {
            id,
            year,
            number_seats,
            number_doors,
            large_bags,
            small_bags,
            odometer,
            brand,
            mark,
            group,
            color,
            type,
            body_type,
            min_price,
            price,
            status,
            currency,
            consumption,
            fuel,
            volume_tank,
            volume_engine,
            transmission,
            options,
            thumbnail,
            thumbnails,
            photos,
            custom_fields,
            wheel_drive,
            registration_number,
            total_price,
            price_before_discount,
            count_days,
            refill,
            mileage_limit
          } = vehicle;

          // Add top features
          const topFeatures = [`${number_seats} Seats`, `${number_doors} Doors`, `Fuel: ${fuel}`, `Transmission: ${transmission}`];

          return {
            id,
            year,
            number_seats,
            number_doors,
            large_bags,
            small_bags,
            odometer,
            mark: `${brand} ${mark}`,
            group,
            color: {
              title: color.title,
              code: color.code
            },
            type,
            body_type,
            min_price,
            price,
            status,
            currency,
            consumption,
            fuel,
            volume_tank,
            volume_engine,
            transmission,
            options: options.map(option => ({
              id: option.id,
              name: option.name,
              icon: option.icon
            })),
            thumbnail,
            thumbnails,
            photos,
            custom_fields,
            wheel_drive,
            registration_number,
            total_price,
            price_before_discount,
            count_days,
            refill,
            mileage_limit,
            topFeatures // Add top features to the vehicle object
          };
        });

        // Apply filters
        let filteredVehicles = parsedVehicles.filter(vehicle => {
          // Check if vehicle has a brand property before accessing it
          const brand = vehicle.brand ? vehicle.brand.toLowerCase() : '';
          const mark = vehicle.mark.toLowerCase();
          const filterLowerCase = filter.toLowerCase();

          // Implement filtering logic based on the brand or mark and the filter state
          return brand.includes(filterLowerCase) || mark.includes(filterLowerCase);
        });

        if (filters.vehicleType !== 'All') {
          filteredVehicles = filteredVehicles.filter(vehicle => vehicle.type === filters.vehicleType);
        }

        if (filters.fuelType !== 'All') {
          filteredVehicles = filteredVehicles.filter(vehicle => vehicle.fuel === filters.fuelType);
        }

        if (filters.transmission !== 'All') {
          filteredVehicles = filteredVehicles.filter(vehicle => vehicle.transmission === filters.transmission);
        }

        if (filters.tripPrice.min !== '' && filters.tripPrice.max !== '') {
          filteredVehicles = filteredVehicles.filter(vehicle => {
            const price = parseFloat(vehicle.price);
            return price >= parseFloat(filters.tripPrice.min) && price <= parseFloat(filters.tripPrice.max);
          });
        }

        // Store the filtered vehicles in the state
        setVehicles(filteredVehicles);
      } catch (error) {
        console.error('Error fetching vehicles:', error);
        setError('Failed to fetch vehicles: ' + error.message);
      } finally {
        setLoading(false);
      }
    };

    fetchVehicles();
  }, [filter, filters]); // Add filter and filters to dependency array

  const handleFilterChange = () => {
    // Filtering logic...
  };

  const resetFilters = () => {
    setFilters(defaultFilters);
  };

  return (
    <div>
      <div className="header-bar"></div>
      <div className="catalog-container">
        <img src="/logo.png" alt="Logo" className="logo" />
        <div className="vehicle-list">
          {loading && <p>Loading Available Vehicles...</p>}
          {error && (
            <div>
              <p>{error}</p>
              <button onClick={() => window.location.reload()}>Retry</button>
            </div>
          )}
          {!loading && !error && vehicles.length === 0 && <p>No vehicles available</p>}
          {!loading && !error && vehicles.length > 0 && (
            <div className="vehicle-grid">
              {vehicles.map(({ id, mark, thumbnail, topFeatures }) => (
                <div key={id} className="vehicle-item">
                  <img src={thumbnail} alt={mark} className="vehicle-thumbnail" />
                  <h3>{mark}</h3>
                  <p className="vehicle-description">{topFeatures.join(' | ')}</p> {/* Display top features */}
                  <p className="vehicle-description">Discover the perfect blend of performance, comfort, and style with our {mark}. With top features like {topFeatures.join(', ')}, our {mark} ensures an unforgettable driving experience.</p> {/* Short description */}
                </div>
              ))}
            </div>
          )}
        </div>
        <div className="sidebar">
          <div className="filter-section">
            <h3>Filter by brand or model</h3>
            <input
              type="text"
              placeholder="Enter brand or model"
              value={filter}
              onChange={e => setFilter(e.target.value)}
              className="filter-input"
            />
          </div>
          <div className="filter-section">
            <h3>Vehicle Type</h3>
            <select
              value={filters.vehicleType}
              onChange={e => setFilters({ ...filters, vehicleType: e.target.value })}
            >
              <option value="All">All</option>
              <option value="Sedan">Sedan</option>
              <option value="SUV">SUV</option>
              <option value="Truck">Truck</option>
            </select>
          </div>
          <div className="filter-section">
            <h3>Fuel Type</h3>
            <select
              value={filters.fuelType}
              onChange={e => setFilters({ ...filters, fuelType: e.target.value })}
            >
              <option value="All">All</option>
              <option value="Gasoline">Gasoline</option>
              <option value="Diesel">Diesel</option>
            </select>
          </div>
          <div className="filter-section">
            <h3>Transmission</h3>
            <select
              value={filters.transmission}
              onChange={e => setFilters({ ...filters, transmission: e.target.value })}
            >
              <option value="All">All</option>
              <option value="Automatic">Automatic</option>
              <option value="Manual">Manual</option>
            </select>
          </div>
          <div className="filter-section">
            <h3>Trip Price</h3>
            <input
              type="range"
              min="0"
              max="1000"
              value={filters.tripPrice.min}
              onChange={e => setFilters({ ...filters, tripPrice: { ...filters.tripPrice, min: e.target.value } })}
              step="10"
            />
            <div className="price-label">
              <span>${filters.tripPrice.min}</span>
              <span>${filters.tripPrice.max}</span>
            </div>
          </div>
          <div className="reset-filter">
            <button onClick={resetFilters}>Reset Filters</button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Step2;
