import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

const Step2 = () => {
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [vehicles, setVehicles] = useState([]);

  useEffect(() => {
    const fetchVehicles = async () => {
      try {
        const step2Data = JSON.parse(localStorage.getItem('step2Data'));
        const token = localStorage.getItem('token');

        const response = await axios.get(`https://calm-retreat-90846-cd036e8a822e.herokuapp.com/https://api.rentsyst.com/v2/booking/search`, {
          params: {
            date_from: step2Data.pickupDateTime,
            date_to: step2Data.returnDateTime,
            pickup_location: step2Data.selectedLocation,
            return_location: step2Data.selectedLocation
          },
          headers: {
            Authorization: `Bearer ${token}`
          }
        });

        console.log('Response data:', response.data);

        const categorizedVehicles = response.data.available_vehicles.map(vehicle => ({
          id: vehicle.id,
          year: vehicle.year,
          number_seats: vehicle.number_seats,
          number_doors: vehicle.number_doors,
          large_bags: vehicle.large_bags,
          small_bags: vehicle.small_bags,
          odometer: vehicle.odometer,
          mark: vehicle.mark,
          group: vehicle.group,
          color: {
            title: vehicle.color.title,
            code: vehicle.color.code
          },
          type: vehicle.type,
          body_type: vehicle.body_type,
          min_price: vehicle.min_price,
          price: vehicle.price,
          status: vehicle.status,
          currency: vehicle.currency,
          consumption: vehicle.consumption,
          fuel: vehicle.fuel,
          volume_tank: vehicle.volume_tank,
          volume_engine: vehicle.volume_engine,
          transmission: vehicle.transmission,
          options: vehicle.options.map(option => ({
            id: option.id,
            name: option.name,
            icon: option.icon
          })),
          thumbnail: vehicle.thumbnail,
          thumbnails: vehicle.thumbnails,
          photos: vehicle.photos,
          custom_fields: {
            owner: vehicle.custom_fields.owner,
            location: vehicle.custom_fields.location
          },
          wheel_drive: vehicle.wheel_drive,
          registration_number: vehicle.registration_number,
          total_price: vehicle.total_price,
          price_before_discount: vehicle.price_before_discount,
          count_days: vehicle.count_days,
          refill: vehicle.refill,
          mileage_limit: vehicle.mileage_limit
        }));

        setVehicles(categorizedVehicles);
      } catch (error) {
        console.error('Error fetching vehicles:', error);
        setError(error.message); // Update error state with error message
      } finally {
        setLoading(false);
      }
    };

    fetchVehicles();
  }, []);

  return (
    <div className="step2">
      <h2>Available Vehicles</h2>
      {loading && <p>Loading...</p>}
      {error && <p>{error}</p>}
      {vehicles && vehicles.length > 0 && (
        <div className="vehicle-list">
          {vehicles.map(vehicle => (
            <div key={vehicle.id} className="vehicle-item">
              <div className="vehicle-thumbnail">
                <img src={vehicle.thumbnail} alt={vehicle.mark} />
              </div>
              <div className="vehicle-details">
                <h3>{vehicle.mark}</h3>
                <p>Year: {vehicle.year}</p>
                <p>Price: {vehicle.price} {vehicle.currency}</p>
                {/* Add more vehicle details as needed */}
              </div>
            </div>
          ))}
        </div>
      )}
      {!loading && !error && (!vehicles || vehicles.length === 0) && <p>No vehicles available</p>}
    </div>
  );
};

export default Step2;
