// src/App.js
import React from 'react';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom'; // If you're using React Router
import { AuthProvider } from './contexts/AuthContext';
import Step1 from './components/Step1'; // Adjust the import path as necessary
// Import other components

function App() {
  return (
    <AuthProvider>
      <Router>
        <div className="App">
          <Switch>
            <Route path="/" exact component={Step1} />
            {/* Define other routes */}
          </Switch>
          {/* More components that might need access to the auth context */}
        </div>
      </Router>
    </AuthProvider>
  );
}

export default App;
