import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios';
import './App.css';
import moment from 'moment';
import './Step2.css';

const defaultFilters = {
  tripPrice: { min: 0, max: 1000 },
  vehicleType: 'All',
  fuelType: 'All',
  transmission: 'All',
  brandOrModel: '',
};

function safeDateFormat(dateInput) {
  if (!dateInput) {
    console.error('Date input is undefined, null, or empty');
    return 'Invalid Date';
  }

  const momentDate = moment(dateInput);

  if (!momentDate.isValid()) { 
    console.error('Invalid date:', dateInput);
    return 'Invalid Date';
  }

  return momentDate.format('MMMM D, YYYY [at] hh:mm A');
}

const formatDate = (dateString) => {
  if (!dateString) return 'Invalid Date';

  const momentDate = moment(dateString);
  if (!momentDate.isValid()) {
    return 'Invalid Date';
  }
  return momentDate.format('MMMM D, YYYY [at] hh:mm A');
};


function validateAndFormatDate(dateString) {
  if (!dateString) return '';

  const date = moment(dateString);
  if (!date.isValid()) {
    console.error('Invalid date:', dateString);
    return '';
  }

  return date.format('YYYY-MM-DD HH:mm:ss');
}

const Step2 = () => {
  const [pickupLocation, setPickupLocation] = useState(localStorage.getItem('pickupLocation') || '');
  const [returnLocation, setReturnLocation] = useState(localStorage.getItem('selectedAddress') || '');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [vehicles, setVehicles] = useState([]);
  const [filter, setFilter] = useState('');
  const [filters, setFilters] = useState(defaultFilters);
  const [currentPrice, setCurrentPrice] = useState(0); // Placeholder, will be updated
  const [maxPrice, setMaxPrice] = useState(0); // Placeholder, will be updated dynamically based on vehicles data
  const [filteredVehicles, setFilteredVehicles] = useState([]); // State to hold filtered vehicles
  const [finalReturnLocation, setFinalReturnLocation] = useState(localStorage.getItem('finalReturnLocation') || '');
  const [dateFrom, setDateFrom] = useState(localStorage.getItem('dateFrom') || '');
  const [dateTo, setDateTo] = useState(localStorage.getItem('dateTo') || '');
  const calculateDuration = () => {
    const from = moment(localStorage.getItem('dateFrom'));
    const to = moment(localStorage.getItem('dateTo'));
    return to.diff(from, 'days');
  };
  const [reservationDuration, setReservationDuration] = useState(calculateDuration());

  const filterVehicles = useCallback(() => {
    const filtered = vehicles.filter(vehicle => {
      const matchesBrandOrModel = filters.brandOrModel ? (vehicle.brand.toLowerCase().includes(filters.brandOrModel.toLowerCase()) || vehicle.mark.toLowerCase().includes(filters.brandOrModel.toLowerCase())) : true;
      const matchesVehicleType = filters.vehicleType === 'All' || vehicle.type === filters.vehicleType;
      const matchesFuelType = filters.fuelType === 'All' || vehicle.fuelType === filters.fuelType;
      const matchesTransmission = filters.transmission === 'All' || vehicle.transmission === filters.transmission;
      const totalPrice = vehicle.price * reservationDuration;
      const withinPriceRange = totalPrice <= currentPrice;

      return matchesBrandOrModel && matchesVehicleType && matchesFuelType && matchesTransmission && withinPriceRange;
    });

    setFilteredVehicles(filtered);
  }, [vehicles, filters, currentPrice, reservationDuration]);

  useEffect(() => {
    filterVehicles();
  }, [filterVehicles]);

  useEffect(() => {
    async function fetchVehicles() {
      setLoading(true);
      setError(null);

      try {
        const token = localStorage.getItem('token');
        if (!token) {
          throw new Error("Token not found in localStorage");
        }

        const storedDateFrom = localStorage.getItem('dateFrom');
        const storedDateTo = localStorage.getItem('dateTo');
        if (!storedDateFrom || !storedDateTo) {
          throw new Error("Date range is incomplete. Please select both start and end dates.");
        }

        const formattedDateFrom = validateAndFormatDate(storedDateFrom);
        const formattedDateTo = validateAndFormatDate(storedDateTo);

        // Retrieve location URLs from localStorage
        const pickupLocationURL = localStorage.getItem('PickupLocationURL');
        const returnLocationURL = localStorage.getItem('ReturnLocationURL');
        const customAddressURL = localStorage.getItem('CustomAddressURL');

        // Ensure there's enough information to proceed with the API call
        if (!customAddressURL && (!pickupLocationURL || !returnLocationURL)) {
          console.error("Location information is missing.");
          setError("Location information is missing. Please select both pickup and return locations.");
          setLoading(false);
          return;
        }

        let queryParams = new URLSearchParams({
          date_from: formattedDateFrom,
          date_to: formattedDateTo,
        });

        // Use CustomAddressURL if available; otherwise, use the specific location URLs
        if (customAddressURL) {
          queryParams.set('pickup_location', customAddressURL);
          queryParams.set('return_location', customAddressURL);
        } else {
          queryParams.set('pickup_location', pickupLocationURL);
          queryParams.set('return_location', returnLocationURL);
        }

        const response = await axios.get(`https://calm-retreat-90846-cd036e8a822e.herokuapp.com/https://api.rentsyst.com/v2/booking/search?${queryParams.toString()}`, {
          headers: { Authorization: `Bearer ${token}` },
        });

        // Process and set the vehicle data
        const vehicleCatalog = response.data.vehicles.map(vehicle => {
          const totalPrice = vehicle.price * reservationDuration; // Calculate total price based on duration
          return {
            ...vehicle,
            totalPrice, // Add totalPrice to each vehicle object
          };
        });
        setVehicles(vehicleCatalog);
        setFilteredVehicles(vehicleCatalog);

        // Calculate the maximum trip price from the filtered vehicles
        const maxTripPrice = Math.max(...vehicleCatalog.map(v => v.totalPrice));
        setMaxPrice(maxTripPrice);
      } catch (error) {
        console.error('Failed to fetch vehicles:', error);
        setError('Failed to fetch available vehicles. Please try again later.');
      } finally {
        setLoading(false);
      }
    }

    fetchVehicles();
  }, [reservationDuration]); // Added reservationDuration to the dependency array

  useEffect(() => {
    localStorage.setItem('finalReturnLocation', finalReturnLocation);
    localStorage.setItem('dateFrom', dateFrom);
    localStorage.setItem('dateTo', dateTo);
  }, [finalReturnLocation, dateFrom, dateTo, reservationDuration]);

  return (
    <div>
      <div className="top-bar" style={{ width: '100%', position: 'relative', paddingTop: '20px', fontSize: '16px' }}>
        <img src="/logo.png" alt="Logo" className="logo" style={{ position: 'absolute', top: '10px', left: '10px', marginTop: '0' }} />
        <div className="reservation-details" style={{ marginLeft: '120px', marginTop: '20px', display: 'flex', alignItems: 'center', color: '#eac831', fontSize: '12px' }}>
          <p>Location: <span className="detail-value">{pickupLocation}</span></p>
          <p>Date From: <span className="detail-value">{safeDateFormat(dateFrom)}</span></p>
          <p>Date To: <span className="detail-value">{safeDateFormat(dateTo)}</span></p>
          <p>Duration: <span className="detail-value">{reservationDuration} days</span></p>
        </div>
      </div>
      <div className="catalog-container" style={{ display: 'flex', justifyContent: 'space-between', paddingTop: '20px' }}>
        <div className="vehicle-list" style={{ zIndex: 9999, width: 'calc(100% - 320px)', height: '100%', overflowY: 'auto', overflowX: 'hidden', msOverflowStyle: 'none', scrollbarWidth: 'none', marginTop: '20px' }}>
          {loading && <p>Loading available vehicles...</p>}
          {error && <p>{error}</p>}
          {!loading && !error && filteredVehicles.length > 0 && (
            <div className="vehicle-grid">
              {filteredVehicles.map((vehicle) => {
                return (
                  <div key={vehicle.id} className="vehicle-item">
                    <img src={vehicle.thumbnail} alt={`${vehicle.brand} ${vehicle.mark}`} className="vehicle-thumbnail" />
                    <div className="vehicle-info">
                      <h3>{`${vehicle.brand} ${vehicle.mark} ${vehicle.year}`}</h3>
                      <p>Color: <span style={{ color: vehicle.color.value }}>{vehicle.color.title}</span></p>
                      <div className="vehicle-features">
                        <h4>Key Features:</h4>
                        <ul>
                          <li>Fuel Efficiency</li>
                          <li>Navigation System</li>
                          <li>Air Conditioning</li>
                          <li>Safety Features</li>
                          <li>Unlimited Mileage</li>
                        </ul>
                      </div>
                      <div className="vehicle-description-container" style={{ height: '15em', overflow: 'hidden', display: '-webkit-box', WebkitLineClamp: '10', WebkitBoxOrient: 'vertical' }}>
                        <p className="vehicle-description">{vehicle.description}</p>
                      </div>
                      <p className="price-info" style={{ color: '#eac831' }}>Daily Price: ${vehicle.price} | Trip Price: ${vehicle.totalPrice}</p>
                    </div>
                  </div>
                );
              })}
            </div>
          )}
        </div>
        <div className="sidebar" style={{ marginTop: '20px', width: '180px' }}>
          <div className="filter-section">
            <h3 className="gold-line">Filter by brand or model</h3>
            <input
              type="text"
              placeholder="Enter brand or model"
              value={filters.brandOrModel}
              onChange={e => setFilters({ ...filters, brandOrModel: e.target.value })}
              className="filter-input"
            />
          </div>
          <div className="filter-section">
            <h3 className="gold-line">Vehicle Type</h3>
            <select
              value={filters.vehicleType}
              onChange={e => setFilters({ ...filters, vehicleType: e.target.value })}
            >
              <option value="All">All</option>
              <option value="Sedan">Sedan</option>
              <option value="SUV">SUV</option>
              <option value="Truck">Truck</option>
            </select>
          </div>
          <div className="filter-section">
            <h3 className="gold-line">Fuel Type</h3>
            <select
              value={filters.fuelType}
              onChange={e => setFilters({ ...filters, fuelType: e.target.value })}
            >
              <option value="All">All</option>
              <option value="Gasoline">Gasoline</option>
              <option value="Diesel">Diesel</option>
            </select>
          </div>
          <div className="filter-section">
            <h3 className="gold-line">Transmission</h3>
            <select
              value={filters.transmission}
              onChange={e => setFilters({ ...filters, transmission: e.target.value })}
            >
              <option value="All">All</option>
              <option value="Automatic">Automatic</option>
              <option value="Manual">Manual</option>
            </select>
          </div>
          <div className="filter-section">
            <h3 className="gold-line">Max Trip Price</h3>
            <input
              type="range"
              min="0"
              max={maxPrice}
              value={currentPrice}
              onChange={(e) => setCurrentPrice(Number(e.target.value))}
              step="10"
            />
            <div className="price-label">
              <span>${currentPrice}</span>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Step2;
