import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

const Step2 = ({ pickupLocationProp, returnLocation, dateFrom, dateTo }) => {
  const defaultFilters = {
    tripPrice: { min: '0', max: '1000' },
    vehicleType: 'All',
    fuelType: 'All',
    transmission: 'All'
  };

  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [vehicles, setVehicles] = useState([]);
  const [filter, setFilter] = useState('');
  const [filters, setFilters] = useState(defaultFilters);
  const [currentPrice, setCurrentPrice] = useState(0);
  const [reservationDuration, setReservationDuration] = useState(0);
  const [pickupLocation, setPickupLocation] = useState('');
  // Remove duplicate declaration of pickupLocation here
  const [dateFromState, setDateFromState] = useState('');
  const [dateToState, setDateToState] = useState('');

  useEffect(() => {
    const fetchVehicles = async () => {
      try {
        const step2Data = JSON.parse(localStorage.getItem('step2Data'));
        if (!step2Data) {
          throw new Error('Step 2 data not found in local storage');
        }

        const { pickupDateTime, returnDateTime, selectedLocation } = step2Data;
        if (!pickupDateTime || !returnDateTime || !selectedLocation) {
          throw new Error('Incomplete step 2 data');
        }

        const fromDateObj = new Date(pickupDateTime);
        const toDateObj = new Date(returnDateTime);
        const options = { year: 'numeric', month: 'long', day: 'numeric', hour: 'numeric', minute: 'numeric', hour12: true };
        const formattedFromDate = fromDateObj.toLocaleDateString('en-US', options);
        const formattedToDate = toDateObj.toLocaleDateString('en-US', options);

        setDateFromState(formattedFromDate);
        setDateToState(formattedToDate);

        const fromDate = encodeURIComponent(pickupDateTime);
        const toDate = encodeURIComponent(returnDateTime);

        const url = `https://calm-retreat-90846-cd036e8a822e.herokuapp.com/https://api.rentsyst.com/v2/booking/search?date_from=${fromDate}&date_to=${toDate}&pickup_location=${selectedLocation}&return_location=${selectedLocation}`;

        const response = await axios.get(url);

        if (!response.data || !response.data.vehicles) {
          throw new Error('Invalid response data format');
        }

        const parsedVehicles = response.data.vehicles.map(vehicle => {
          const {
            id,
            mark,
            thumbnail,
            topFeatures,
            price,
            type,
            fuel,
            transmission
          } = vehicle;

          const features = topFeatures ? topFeatures.join(', ') : 'N/A';

          return {
            id,
            mark,
            thumbnail,
            topFeatures: features,
            description: `Experience the ultimate in luxury and efficiency with our ${mark}. Key features include ${features}, making every journey memorable and comfortable.`,
            price: typeof price === 'string' ? price : String(price),
            type,
            fuel,
            transmission
          };
        });

        let filteredVehicles = parsedVehicles.filter(vehicle => {
          const brand = vehicle.mark ? vehicle.mark.toLowerCase() : '';
          const filterLowerCase = filter.toLowerCase();
          return brand.includes(filterLowerCase);
        });

        if (filters.vehicleType !== 'All') {
          filteredVehicles = filteredVehicles.filter(vehicle => vehicle.type === filters.vehicleType);
        }

        if (filters.fuelType !== 'All') {
          filteredVehicles = filteredVehicles.filter(vehicle => vehicle.fuel === filters.fuelType);
        }

        if (filters.transmission !== 'All') {
          filteredVehicles = filteredVehicles.filter(vehicle => vehicle.transmission === filters.transmission);
        }

        if (filters.tripPrice.min !== '' && filters.tripPrice.max !== '') {
          filteredVehicles = filteredVehicles.filter(vehicle => {
            const price = parseFloat(vehicle.price.substring(1));
            const pickupDate = new Date(pickupDateTime);
            const returnDate = new Date(returnDateTime);
            const timeDifference = returnDate.getTime() - pickupDate.getTime();
            const hoursDifference = timeDifference / (1000 * 3600);
            const blocksOf24Hours = Math.ceil(hoursDifference / 24);
            const totalTripPrice = price * blocksOf24Hours;
            return totalTripPrice >= parseFloat(filters.tripPrice.min) && totalTripPrice <= parseFloat(filters.tripPrice.max);
          });

          const minPrice = Math.min(...filteredVehicles.map(vehicle => parseFloat(vehicle.price.substring(1))));
          const maxPrice = Math.max(...filteredVehicles.map(vehicle => parseFloat(vehicle.price.substring(1))));

          setFilters({
            ...filters,
            tripPrice: {
              min: String(minPrice),
              max: String(maxPrice)
            }
          });
        }

        setVehicles(filteredVehicles);

        // Calculate reservation duration
        const diffTime = Math.abs(toDateObj - fromDateObj);
        const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));
        setReservationDuration(diffDays);
      } catch (error) {
        console.error('Error fetching vehicles:', error);
        setError('Failed to fetch vehicles: ' + error.message);
      } finally {
        setLoading(false);
      }
    };

    fetchVehicles();
  }, [filter, filters]);

  useEffect(() => {
    const dateFrom = localStorage.getItem('date_from');
    setDateFromState(dateFrom);
  }, []);

  useEffect(() => {
    const dateTo = localStorage.getItem('date_to');
    setDateToState(dateTo);
  }, []);

  const resetFilters = () => {
    setFilter('');
    setFilters(defaultFilters);
    setCurrentPrice(0); // Reset current price
  };

  const reserveNow = (vehicleId) => {
    console.log(`Reserving vehicle with ID: ${vehicleId}`);
  };

  const handleTripPriceChange = (e) => {
    const value = e.target.value;
    console.log('Trip price slider value:', value);
    setCurrentPrice(value);  
    setFilters(prevFilters => ({
      ...prevFilters,
      tripPrice: {
        ...prevFilters.tripPrice,
        max: value
      }
    }));
  };

  return (
    <div>
      <div className="top-bar" style={{ position: 'relative', paddingTop: '20px' }}>
        <img src="/logo.png" alt="Logo" className="logo" style={{ position: 'absolute', top: '10px', left: '10px', marginTop: '0' }} />
        <div className="reservation-details" style={{ marginLeft: '120px', marginTop: '20px' }}>
          <p>Pickup Location: {pickupLocationProp}</p>
          <p>Return Location: {returnLocation}</p>
          <p>Date From: {dateFromState}</p>
          <p>Date To: {dateToState}</p>
          <p>Reservation Duration: {reservationDuration} days</p>
        </div>
      </div>
      <div className="catalog-container" style={{ display: 'flex', justifyContent: 'space-between', paddingTop: '20px' }}>
        <div className="vehicle-list" style={{ zIndex: 9999, width: 'calc(100% - 320px)', height: '100%', overflowY: 'auto', overflowX: 'hidden', msOverflowStyle: 'none', scrollbarWidth: 'none', marginTop: '20px' }}>
          {!loading && !error && vehicles.length > 0 && (
            <div className="vehicle-grid">
              {vehicles.map(({ id, mark, thumbnail, topFeatures, description, price }) => (
                <div key={id} className="vehicle-item" style={{ maxWidth: '300px', maxHeight: '400px', overflow: 'hidden', position: 'relative', marginTop: '20px' }}>
                  <div className="vehicle-thumbnail-container">
                    <img src={thumbnail} alt={mark} className="vehicle-thumbnail" />
                  </div>
                  <h3>{mark}</h3>
                  <p>{description}</p>
                  {topFeatures && Array.isArray(topFeatures) && (
                    <div className="best-features">
                      <h4>Popular Features:</h4>
                      <ul>
                        {topFeatures.map((feature, index) => (
                          <li key={index}>{feature}</li>
                        ))}
                      </ul>
                    </div>
                  )}
                  <div className="price-info">
                    <p>Daily Price: ${price}</p>
                  </div>
                  <button className="reserve-button" onClick={() => reserveNow(id)}>Reserve Now</button>
                </div>
              ))}
            </div>
          )}
        </div>
        <div className="sidebar" style={{ marginTop: '20px' }}>
          <div className="filter-section">
            <h3>Filter by brand or model</h3>
            <input
              type="text"
              placeholder="Enter brand or model"
              value={filter}
              onChange={e => setFilter(e.target.value)}
              className="filter-input"
            />
          </div>
          <div className="filter-section">
            <h3>Vehicle Type</h3>
            <select
              value={filters.vehicleType}
              onChange={e => setFilters({ ...filters, vehicleType: e.target.value })}
            >
              <option value="All">All</option>
              <option value="Sedan">Sedan</option>
              <option value="SUV">SUV</option>
              <option value="Truck">Truck</option>
            </select>
          </div>
          <div className="filter-section">
            <h3>Fuel Type</h3>
            <select
              value={filters.fuelType}
              onChange={e => setFilters({ ...filters, fuelType: e.target.value })}
            >
              <option value="All">All</option>
              <option value="Gasoline">Gasoline</option>
              <option value="Diesel">Diesel</option>
            </select>
          </div>
          <div className="filter-section">
            <h3>Transmission</h3>
            <select
              value={filters.transmission}
              onChange={e => setFilters({ ...filters, transmission: e.target.value })}
            >
              <option value="All">All</option>
              <option value="Automatic">Automatic</option>
              <option value="Manual">Manual</option>
            </select>
          </div>
          <div className="filter-section">
            <h3>Trip Price</h3>
            <input
              type="range"
              min={filters.tripPrice.min}
              max={filters.tripPrice.max}
              value={currentPrice}
              onChange={handleTripPriceChange}
              step="10"
            />
            <div className="price-label">
              <span>${currentPrice}</span>
            </div>
          </div>
          <div className="reset-filter">
            <button onClick={resetFilters}>Reset Filters</button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Step2;
