import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

const Step2 = () => {
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [vehicles, setVehicles] = useState([]);
  const [filter, setFilter] = useState('');
  const [filters, setFilters] = useState({
    priceRange: { min: '', max: '' },
    vehicleType: 'All',
    fuelType: []
  });

  useEffect(() => {
    const fetchVehicles = async () => {
      try {
        const step2Data = JSON.parse(localStorage.getItem('step2Data'));
        const token = localStorage.getItem('token');

        // Extracting parameters from step2Data
        const { pickupDateTime, returnDateTime, selectedLocation } = step2Data;

        // Format date_from and date_to
        const fromDate = encodeURIComponent(pickupDateTime);
        const toDate = encodeURIComponent(returnDateTime);

        // Constructing URL with parameters
        const url = `https://calm-retreat-90846-cd036e8a822e.herokuapp.com/https://api.rentsyst.com/v2/booking/search?date_from=${fromDate}&date_to=${toDate}&pickup_location=${selectedLocation}&return_location=${selectedLocation}`;

        const response = await axios.get(url, {
          headers: {
            Authorization: `Bearer ${token}`
          }
        });

        console.log('Response data:', response.data);

        // Check if response data and search exist
        if (!response.data || !response.data.vehicles) {
          throw new Error('Invalid response data format');
        }

        // Parse the vehicles from the response
        const parsedVehicles = response.data.vehicles.map(vehicle => {
          const {
            id,
            year,
            number_seats,
            number_doors,
            large_bags,
            small_bags,
            odometer,
            brand,
            mark,
            group,
            color,
            type,
            body_type,
            min_price,
            price,
            status,
            currency,
            consumption,
            fuel,
            volume_tank,
            volume_engine,
            transmission,
            options,
            thumbnail,
            thumbnails,
            photos,
            custom_fields,
            wheel_drive,
            registration_number,
            total_price,
            price_before_discount,
            count_days,
            refill,
            mileage_limit
          } = vehicle;

          // Add top features
          const topFeatures = [`${number_seats} Seats`, `${number_doors} Doors`, `Fuel: ${fuel}`, `Transmission: ${transmission}`];

          return {
            id,
            year,
            number_seats,
            number_doors,
            large_bags,
            small_bags,
            odometer,
            mark: `${brand} ${mark}`,
            group,
            color: {
              title: color.title,
              code: color.code
            },
            type,
            body_type,
            min_price,
            price,
            status,
            currency,
            consumption,
            fuel,
            volume_tank,
            volume_engine,
            transmission,
            options: options.map(option => ({
              id: option.id,
              name: option.name,
              icon: option.icon
            })),
            thumbnail,
            thumbnails,
            photos,
            custom_fields,
            wheel_drive,
            registration_number,
            total_price,
            price_before_discount,
            count_days,
            refill,
            mileage_limit,
            topFeatures // Add top features to the vehicle object
          };
        });

        // Store the parsed vehicles in the state
        setVehicles(parsedVehicles);
      } catch (error) {
        console.error('Error fetching vehicles:', error);
        setError('Failed to fetch vehicles: ' + error.message);
      } finally {
        setLoading(false);
      }
    };

    fetchVehicles();
  }, []); // Add empty dependency array to useEffect

  console.log('Loading:', loading);
  console.log('Error:', error);
  console.log('Vehicles:', vehicles);

  const filteredVehicles = vehicles.filter(vehicle => {
    // Check if vehicle has a brand property before accessing it
    const brand = vehicle.brand ? vehicle.brand.toLowerCase() : '';
    const mark = vehicle.mark.toLowerCase();
    const filterLowerCase = filter.toLowerCase();

    // Implement filtering logic based on the brand or mark and the filter state
    return brand.includes(filterLowerCase) || mark.includes(filterLowerCase);
  });

  // Function to handle changes in filters and pass them to parent component
  const handleFilterChange = () => {
    // Perform any additional processing if needed
    console.log('Filters:', filters);
    // You can apply the filters here or pass them to another component for processing
  };

  return (
    <div className="catalog-container">
      <img src="/logo.png" alt="Logo" className="logo" />
      <div className="vehicle-list">
        {loading && <p>Loading Available Vehicles...</p>}
        {error && (
          <div>
            <p>{error}</p>
            <button onClick={() => window.location.reload()}>Retry</button>
          </div>
        )}
        {!loading && !error && filteredVehicles.length === 0 && <p>No vehicles available</p>}
        {!loading && !error && filteredVehicles.length > 0 && (
          <div className="vehicle-grid">
            {filteredVehicles.map(({ id, mark, thumbnail, topFeatures }) => (
              <div key={id} className="vehicle-item">
                <img src={thumbnail} alt={mark} className="vehicle-thumbnail" />
                <h3>{mark}</h3>
                <p className="vehicle-description">{topFeatures.join(' | ')}</p> {/* Display top features */}
                <p className="vehicle-description">Discover the perfect blend of performance, comfort, and style with our {mark}. With top features like {topFeatures.join(', ')}, our {mark} ensures an unforgettable driving experience.</p> {/* Short description */}
              </div>
            ))}
          </div>
        )}
      </div>
      <div className="sidebar">
        <h2>Filters</h2>
        <div className="filter-section">
          <h3>Filter by brand or model</h3>
          <input
            type="text"
            placeholder="Enter brand or model"
            value={filter}
            onChange={e => setFilter(e.target.value)}
            className="filter-input"
          />
        </div>
        <div className="filter-section">
          <h3>Price Range</h3>
          <input
            type="range"
            min="0"
            max="1000"
            value={filters.priceRange.min}
            onChange={e => setFilters({ ...filters, priceRange: { ...filters.priceRange, min: e.target.value } })}
          />
          <input
            type="range"
            min="0"
            max="1000"
            value={filters.priceRange.max}
            onChange={e => setFilters({ ...filters, priceRange: { ...filters.priceRange, max: e.target.value } })}
          />
        </div>
        <div className="filter-section">
          <h3>Vehicle Type</h3>
          <select
            value={filters.vehicleType}
            onChange={e => setFilters({ ...filters, vehicleType: e.target.value })}
          >
            <option value="All">All</option>
            <option value="Sedan">Sedan</option>
            <option value="SUV">SUV</option>
            <option value="Truck">Truck</option>
          </select>
        </div>
        <div className="filter-section">
          <h3>Fuel Type</h3>
          <label>
            <input
              type="checkbox"
              value="Gasoline"
              checked={filters.fuelType.includes('Gasoline')}
              onChange={e => {
                const checked = e.target.checked;
                setFilters(prevState => ({
                  ...prevState,
                  fuelType: checked
                    ? [...prevState.fuelType, e.target.value]
                    : prevState.fuelType.filter(type => type !== e.target.value)
                }));
              }}
            />
            Gasoline
          </label>
          <label>
            <input
              type="checkbox"
              value="Diesel"
              checked={filters.fuelType.includes('Diesel')}
              onChange={e => {
                const checked = e.target.checked;
                setFilters(prevState => ({
                  ...prevState,
                  fuelType: checked
                    ? [...prevState.fuelType, e.target.value]
                    : prevState.fuelType.filter(type => type !== e.target.value)
                }));
              }}
            />
            Diesel
          </label>
        </div>
        <button onClick={handleFilterChange}>Apply Filters</button>
      </div>
    </div>
  );
};

export default Step2;
