import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

const Step2 = ({ pickupLocationProp, returnLocation, dateTo }) => {
  const defaultFilters = {
    tripPrice: { min: '0', max: '1000' },
    vehicleType: 'All',
    fuelType: 'All',
    transmission: 'All'
  };

  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [vehicles, setVehicles] = useState([]);
  const [filter, setFilter] = useState('');
  const [filters, setFilters] = useState(defaultFilters);
  const [currentPrice, setCurrentPrice] = useState(0);

  const resetFilters = () => {
    setFilter('');
    setFilters(defaultFilters);
    setCurrentPrice(0);
  };

  const handleTripPriceChange = (e) => {
    const value = e.target.value;
    setCurrentPrice(value);
    setFilters(prevFilters => ({
      ...prevFilters,
      tripPrice: {
        ...prevFilters.tripPrice,
        max: value
      }
    }));
  };

  useEffect(() => {
    const fetchVehicles = async () => {
      try {
        const step2Data = JSON.parse(localStorage.getItem('step2Data'));
        const token = localStorage.getItem('token');

        const { pickupDateTime, returnDateTime, selectedLocation } = step2Data ?? {};
        if (!pickupDateTime || !returnDateTime || !selectedLocation) {
          throw new Error('Invalid step 2 data');
        }

        const fromDateObj = new Date(pickupDateTime);
        const toDateObj = new Date(returnDateTime);
        const options = { year: 'numeric', month: 'long', day: 'numeric', hour: 'numeric', minute: 'numeric', hour12: true };
        const formattedFromDate = fromDateObj.toLocaleDateString('en-US', options);
        const formattedToDate = toDateObj.toLocaleDateString('en-US', options);

        // Set the pickup and return locations
        const pickupLocation = localStorage.getItem('pickupLocation');
        const returnLocation = localStorage.getItem('returnLocation');

        // Set state variables
        setDateFrom(formattedFromDate);
        setSelectedDateTo(formattedToDate);
        setPickupLocation(pickupLocation);
        setReturnLocation(returnLocation);

        const fromDate = encodeURIComponent(pickupDateTime);
        const toDate = encodeURIComponent(returnDateTime);

        const url = `YOUR_API_ENDPOINT?date_from=${fromDate}&date_to=${toDate}&pickup_location=${pickupLocation}&return_location=${returnLocation}`;

        const response = await axios.get(url, {
          headers: {
            Authorization: `Bearer ${token}`
          }
        });

        if (!response.data || !response.data.vehicles) {
          throw new Error('Invalid response data format');
        }

        let parsedVehicles = response.data.vehicles.map(vehicle => {
          const {
            id,
            mark,
            thumbnail,
            topFeatures,
            price,
            type,
            fuel,
            transmission
          } = vehicle;

          const features = topFeatures ? topFeatures.join(', ') : 'N/A';

          return {
            id,
            mark,
            thumbnail,
            topFeatures: features,
            description: `Experience the ultimate in luxury and efficiency with our ${mark}. Key features include ${features}, making every journey memorable and comfortable.`,
            price: typeof price === 'string' ? price : String(price),
            type,
            fuel,
            transmission
          };
        });

        // Apply filters
        parsedVehicles = parsedVehicles.filter(vehicle => {
          const { vehicleType, fuelType, transmission, tripPrice } = filters;

          if (vehicleType !== 'All' && vehicle.type !== vehicleType) return false;
          if (fuelType !== 'All' && vehicle.fuel !== fuelType) return false;
          if (transmission !== 'All' && vehicle.transmission !== transmission) return false;

          const price = parseFloat(vehicle.price.substring(1));
          const pickupDate = new Date(pickupDateTime);
          const returnDate = new Date(returnDateTime);
          const timeDifference = returnDate.getTime() - pickupDate.getTime();
          const hoursDifference = timeDifference / (1000 * 3600);
          const blocksOf24Hours = Math.ceil(hoursDifference / 24);
          const totalTripPrice = price * blocksOf24Hours;

          if (tripPrice.min !== '' && tripPrice.max !== '' && (totalTripPrice < parseFloat(tripPrice.min) || totalTripPrice > parseFloat(tripPrice.max))) return false;

          return true;
        });

        setVehicles(parsedVehicles);
      } catch (error) {
        console.error('Error fetching vehicles:', error);
        setError('Failed to fetch vehicles: ' + error.message);
      } finally {
        setLoading(false);
      }
    };

    fetchVehicles();
  }, []);

  const reserveNow = async (vehicleId) => {
    localStorage.setItem('selected_vehicle_id', vehicleId);
    window.location.href = '/step3';
  };

  return (
    <div>
      <div className="top-bar" style={{ position: 'relative', paddingTop: '20px' }}>
        <img src="/logo.png" alt="Logo" className="logo" style={{ position: 'absolute', top: '10px', left: '10px', marginTop: '0' }} />
        <div className="reservation-details" style={{ marginLeft: '120px', marginTop: '20px' }}>
          <p>Pickup Location: {pickupLocation}</p>
          <p>Return Location: {returnLocation}</p>
          <p>Date From: {dateFrom}</p>
          <p>Date To: {dateTo}</p>
          {/* <p>Reservation Duration: {reservationDuration} days</p> */}
        </div>
      </div>
      <div className="catalog-container" style={{ display: 'flex', justifyContent: 'space-between', paddingTop: '20px' }}>
        <div className="vehicle-list" style={{ zIndex: 9999, width: 'calc(100% - 320px)', height: '100%', overflowY: 'auto', overflowX: 'hidden', msOverflowStyle: 'none', scrollbarWidth: 'none', marginTop: '20px' }}>
          {!loading && !error && vehicles.length > 0 && (
            <div className="vehicle-grid">
              {vehicles.map(({ id, mark, thumbnail, topFeatures, description, price }) => (
                <div key={id} className="vehicle-item" style={{ maxWidth: '300px', maxHeight: '400px', overflow: 'hidden', position: 'relative', marginTop: '20px' }}>
                  <div className="vehicle-thumbnail-container">
                    <img src={thumbnail} alt={mark} className="vehicle-thumbnail" />
                  </div>
                  <h3>{mark}</h3>
                  <p>{description}</p>
                  {topFeatures && Array.isArray(topFeatures) && (
                    <div className="best-features">
                      <h4>Popular Features:</h4>
                      <ul>
                        {topFeatures.map((feature, index) => (
                          <li key={index}>{feature}</li>
                        ))}
                      </ul>
                    </div>
                  )}
                  <div className="price-info">
                    <p>Daily Price: ${price}</p>
                  </div>
                  <button className="reserve-button" onClick={() => reserveNow(id)}>Reserve Now</button>
                </div>
              ))}
            </div>
          )}
          {!loading && error && <p>{error}</p>}
          {!loading && !error && vehicles.length === 0 && <p>No vehicles available for the selected criteria.</p>}
        </div>
        <div className="sidebar" style={{ marginTop: '20px' }}>
          {/* Filter sections */}
        </div>
      </div>
    </div>
  );
};

export default Step2;
