import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useLocation } from 'react-router-dom'; // Import useLocation
import './App.css';

// Define defaultFilters before using it
const defaultFilters = {
  tripPrice: { min: 0, max: 1000 },
  vehicleType: 'All',
  fuelType: 'All',
  transmission: 'All',
};

const formatDate = (dateString) => {
  const options = { year: 'numeric', month: 'long', day: 'numeric' };
  return new Intl.DateTimeFormat('en-US', options).format(new Date(dateString));
};

const Step2 = () => {
  const location = useLocation();
  const { pickupLocation, returnLocation, dateFrom, dateTo, reservationDuration } = location.state || {};

  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [vehicles, setVehicles] = useState([]);
  const [filter, setFilter] = useState('');
  const [filters, setFilters] = useState(defaultFilters);
  const [currentPrice, setCurrentPrice] = useState(0);
  const [filteredVehicles, setFilteredVehicles] = useState([]); // State to hold filtered vehicles
  const displayDuration = `${reservationDuration} days`;

  const resetFilters = () => {
    setFilter('');
    setFilters(defaultFilters);
    setCurrentPrice(0);
  };

  useEffect(() => {
    const fetchVehicles = async () => {
      try {
        const response = await axios.get('api/vehicles');
        setVehicles(response.data); // Assuming the response data is an array of vehicles
        filterVehiclesByPrice(response.data); // Filter vehicles right after fetching
      } catch (error) {
        console.error('Error fetching vehicles:', error);
        setError('Failed to fetch vehicles: ' + error.message);
      } finally {
        setLoading(false);
      }
    };

    fetchVehicles();
  }, []);

  // Function to filter vehicles based on total price
  const filterVehiclesByPrice = (vehicles) => {
    const filtered = vehicles.filter(vehicle => {
      const totalPrice = vehicle.price * reservationDuration; // Calculate total price
      return totalPrice <= currentPrice; // Compare with currentPrice (or any other logic you need)
    });
    setFilteredVehicles(filtered);
  };

  // Call filterVehiclesByPrice whenever currentPrice or reservationDuration changes
  useEffect(() => {
    filterVehiclesByPrice(vehicles);
  }, [currentPrice, reservationDuration, vehicles]);

  // Adjust the handleTripPriceChange function to set currentPrice based on input
  const handleTripPriceChange = (event) => {
    setCurrentPrice(event.target.value);
  };

  // Placeholder reserveNow function
  const reserveNow = (vehicleId) => {
    // Placeholder functionality - do nothing or log a message
    console.log(`Reserve vehicle with ID: ${vehicleId}`);
  };

  return (
    <div>
      <div className="top-bar" style={{ position: 'relative', paddingTop: '20px' }}>
        <img src="/logo.png" alt="Logo" className="logo" style={{ position: 'absolute', top: '20px', left: '10px', marginTop: '0' }} />
        <div className="reservation-details" style={{ marginLeft: '120px', marginTop: '20px' }}>
          <p>Pickup Location: {pickupLocation || 'Not specified'}</p>
          <p>Return Location: {returnLocation || 'Not specified'}</p>
          <p>Date From: {dateFrom ? formatDate(dateFrom) : 'Not specified'}</p>
          <p>Date To: {dateTo ? formatDate(dateTo) : 'Not specified'}</p>
          {reservationDuration > 0 ? <p>Reservation Duration: {reservationDuration} days</p> : <p>Reservation Duration: Not specified</p>}
        </div>
      </div>
      <div className="catalog-container" style={{ display: 'flex', justifyContent: 'space-between', paddingTop: '20px' }}>

        <div className="vehicle-list" style={{ zIndex: 9999, width: 'calc(100% - 320px)', height: '100%', overflowY: 'auto', overflowX: 'hidden', msOverflowStyle: 'none', scrollbarWidth: 'none', marginTop: '20px' }}>
          {!loading && !error && filteredVehicles.length > 0 && (
            <div className="vehicle-grid">
              {filteredVehicles.map(({ id, mark, thumbnail, topFeatures, description, price }) => (
                <div key={id} className="vehicle-item" style={{ maxWidth: '300px', maxHeight: '400px', overflow: 'hidden', position: 'relative', marginTop: '20px' }}>
                  <div className="vehicle-thumbnail-container">
                    <img src={thumbnail} alt={mark} className="vehicle-thumbnail" />
                  </div>
                  <h3>{mark}</h3>
                  <p>{description}</p>
                  {topFeatures && Array.isArray(topFeatures) && (
                    <div className="best-features">
                      <h4>Popular Features:</h4>
                      <ul>
                        {topFeatures.map((feature, index) => (
                          <li key={index}>{feature}</li>
                        ))}
                      </ul>
                    </div>
                  )}
                  <div className="price-info">
                    <p>Daily Price: ${price}</p>
                  </div>
                  <button className="reserve-button" onClick={() => reserveNow(id)}>Reserve Now</button>
                </div>
              ))}
            </div>
          )}
        </div>
        <div className="sidebar" style={{ marginTop: '20px' }}>
          {/* Filter by brand or model */}
          <div className="filter-section">
            <h3>Filter by brand or model</h3>
            <input
              type="text"
              placeholder="Enter brand or model"
              value={filter}
              onChange={e => setFilter(e.target.value)}
              className="filter-input"
            />
          </div>
          {/* Vehicle Type filter */}
          <div className="filter-section">
            <h3>Vehicle Type</h3>
            <select
              value={filters.vehicleType}
              onChange={e => setFilters({ ...filters, vehicleType: e.target.value })}
            >
              <option value="All">All</option>
              <option value="Sedan">Sedan</option>
              <option value="SUV">SUV</option>
              <option value="Truck">Truck</option>
            </select>
          </div>
          {/* Fuel Type filter */}
          <div className="filter-section">
            <h3>Fuel Type</h3>
            <select
              value={filters.fuelType}
              onChange={e => setFilters({ ...filters, fuelType: e.target.value })}
            >
              <option value="All">All</option>
              <option value="Gasoline">Gasoline</option>
              <option value="Diesel">Diesel</option>
            </select>
          </div>
          {/* Transmission filter */}
          <div className="filter-section">
            <h3>Transmission</h3>
            <select
              value={filters.transmission}
              onChange={e => setFilters({ ...filters, transmission: e.target.value })}
            >
              <option value="All">All</option>
              <option value="Automatic">Automatic</option>
              <option value="Manual">Manual</option>
            </select>
          </div>
          {/* Trip Price filter */}
          <div className="filter-section">
            <h3>Max Total Price</h3>
            <input
              type="range"
              min="0"
              max="10000" // Adjust max value as needed
              value={currentPrice}
              onChange={handleTripPriceChange}
              step="10"
            />
            <div className="price-label">
              <span>${currentPrice}</span>
            </div>
          </div>
          {/* Reset Filters button */}
          <div className="reset-filter">
            <button onClick={resetFilters}>Reset Filters</button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Step2;
