import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

const Step2 = () => {
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [vehicles, setVehicles] = useState([]);

  useEffect(() => {
    const fetchVehicles = async () => {
      try {
        const step2Data = JSON.parse(localStorage.getItem('step2Data'));
        const token = localStorage.getItem('token');

        // Extracting parameters from step2Data
        const { pickupDateTime, returnDateTime, selectedLocation } = step2Data;

        // Format date_from and date_to
        const fromDate = encodeURIComponent(pickupDateTime);
        const toDate = encodeURIComponent(returnDateTime);

        // Constructing URL with parameters
        const url = `https://calm-retreat-90846-cd036e8a822e.herokuapp.com/https://api.rentsyst.com/v2/booking/search?date_from=${fromDate}&date_to=${toDate}&pickup_location=${selectedLocation}&return_location=${selectedLocation}`;

        const response = await axios.get(url, {
          headers: {
            Authorization: `Bearer ${token}`
          }
        });

        console.log('Response data:', response.data);

        // Parse the available vehicles from the response
        const availableVehicles = response.data.available_vehicles.map(vehicle => ({
          id: vehicle.id,
          year: vehicle.year,
          number_seats: vehicle.number_seats,
          number_doors: vehicle.number_doors,
          large_bags: vehicle.large_bags,
          small_bags: vehicle.small_bags,
          odometer: vehicle.odometer,
          mark: vehicle.mark,
          group: vehicle.group,
          color: {
            title: vehicle.color.title,
            code: vehicle.color.code
          },
          type: vehicle.type,
          body_type: vehicle.body_type,
          min_price: vehicle.min_price,
          price: vehicle.price,
          status: vehicle.status,
          currency: vehicle.currency,
          consumption: vehicle.consumption,
          fuel: vehicle.fuel,
          volume_tank: vehicle.volume_tank,
          volume_engine: vehicle.volume_engine,
          transmission: vehicle.transmission,
          options: vehicle.options.map(option => ({
            id: option.id,
            name: option.name,
            icon: option.icon
          })),
          thumbnail: vehicle.thumbnail,
          thumbnails: vehicle.thumbnails,
          photos: vehicle.photos,
          custom_fields: vehicle.custom_fields,
          wheel_drive: vehicle.wheel_drive,
          registration_number: vehicle.registration_number,
          total_price: vehicle.total_price,
          price_before_discount: vehicle.price_before_discount,
          count_days: vehicle.count_days,
          refill: vehicle.refill,
          mileage_limit: vehicle.mileage_limit
        }));

        // Store the parsed vehicles in the state
        setVehicles(availableVehicles);
      } catch (error) {
        console.error('Error fetching vehicles:', error);
        setError('Failed to fetch vehicles: ' + error.message);
      } finally {
        setLoading(false);
      }
    };

    fetchVehicles();
  }, []);

  console.log('Loading:', loading);
  console.log('Error:', error);
  console.log('Vehicles:', vehicles);

  return (
    <div className="step2">
      <div className="catalog">
        <h2>Available Vehicles</h2>
        {loading && <p>Loading...</p>}
        {error && <p>{error}</p>}
        {!loading && !error && vehicles.length === 0 && <p>No vehicles available</p>}
        {!loading && !error && vehicles.length > 0 && (
          <div className="vehicle-list">
            {vehicles.map(vehicle => (
              <div key={vehicle.id} className="vehicle-item">
                <div className="vehicle-thumbnail">
                  <img src={vehicle.thumbnail} alt={`${vehicle.mark}`} />
                </div>
                <div className="vehicle-details">
                  <h3>{`${vehicle.mark}`}</h3>
                  <p>ID: {vehicle.id}</p>
                  <p>Year: {vehicle.year}</p>
                  <p>Number of Seats: {vehicle.number_seats}</p>
                  {/* Render other vehicle details as needed */}
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default Step2;
