useEffect(() => {
  const fetchVehicles = async () => {
    try {
      const step2Data = JSON.parse(localStorage.getItem('step2Data'));
      const token = localStorage.getItem('token');

      const response = await axios.get(`https://calm-retreat-90846-cd036e8a822e.herokuapp.com/https://api.rentsyst.com/v2/booking/search`, {
        params: {
          date_from: step2Data.pickupDateTime,
          date_to: step2Data.returnDateTime,
          pickup_location: step2Data.selectedLocation,
          return_location: step2Data.selectedLocation
        },
        headers: {
          Authorization: `Bearer ${token}`
        }
      });

      // Assuming the response structure is similar to the provided example
      const categorizedVehicles = response.data.map(vehicle => ({
        id: vehicle.id,
        name: vehicle.name,
        description: vehicle.description,
        thumbnails: vehicle.thumbnails.map(photo => photo.url), // Extract thumbnail URLs
        dailyPrice: vehicle.dailyPrice,
        features: vehicle.features.map(feature => feature.name),
        // Add more categories as needed
      }));

      setVehicles(categorizedVehicles);
      setLoading(false); // Set loading to false after successful fetch
    } catch (error) {
      console.error('Error fetching vehicles:', error);
      setError('Failed to fetch vehicles. Please try again later.');
      setLoading(false); // Set loading to false in case of error
    }
  };

  fetchVehicles();
}, []);

// Inside the return statement
{vehicles.map(vehicle => (
  <div key={vehicle.id} className="vehicle-item">
    <h3>{vehicle.name}</h3>
    <p>{vehicle.description}</p>
    <div className="thumbnails">
      {vehicle.thumbnails.map((thumbnail, index) => (
        <img key={index} src={thumbnail} alt={`Thumbnail ${index + 1}`} />
      ))}
    </div>
    {/* Render other vehicle details */}
  </div>
))}
