import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

const Step2 = () => {
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [vehicles, setVehicles] = useState([]);

  useEffect(() => {
    const fetchVehicles = async () => {
      try {
        const step2Data = JSON.parse(localStorage.getItem('step2Data'));
        const token = localStorage.getItem('token');

        const response = await axios.get(`https://calm-retreat-90846-cd036e8a822e.herokuapp.com/https://api.rentsyst.com/v2/booking/search`, {
          params: {
            date_from: step2Data.pickupDateTime,
            date_to: step2Data.returnDateTime,
            pickup_location: step2Data.selectedLocation,
            return_location: step2Data.selectedLocation
          },
          headers: {
            Authorization: `Bearer ${token}`
          }
        });

        console.log('Response data:', response.data);

        const vehicleData = response.data.available_vehicles || [];

        const mappedVehicles = vehicleData.map(vehicle => ({
          id: vehicle.id,
          year: vehicle.year,
          price: vehicle.price,
          currency: vehicle.currency,
          mark: vehicle.mark,
          thumbnail: vehicle.thumbnail
        }));

        setVehicles(mappedVehicles);
      } catch (error) {
        console.error('Error fetching vehicles:', error);
        setError('Failed to fetch vehicles');
      } finally {
        setLoading(false);
      }
    };

    fetchVehicles();
  }, []);

  return (
    <div className="step2">
      <h2>Available Vehicles</h2>
      {loading && <p>Loading...</p>}
      {error && <p>{error}</p>}
      {vehicles && vehicles.length > 0 && (
        <div className="vehicle-list">
          {vehicles.map(vehicle => (
            <div key={vehicle.id} className="vehicle-item">
              <div className="vehicle-thumbnail">
                <img src={vehicle.thumbnail} alt={vehicle.mark} />
              </div>
              <div className="vehicle-details">
                <h3>{vehicle.mark}</h3>
                <p>Year: {vehicle.year}</p>
                <p>Price: {vehicle.price} {vehicle.currency}</p>
                {/* Add more vehicle details as needed */}
              </div>
            </div>
          ))}
        </div>
      )}
      {!loading && !error && vehicles.length === 0 && <p>No vehicles available</p>}
    </div>
  );
};

export default Step2;
