import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

const Step2 = () => {
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [vehicles, setVehicles] = useState([]);
  const [filteredVehicles, setFilteredVehicles] = useState([]);
  const [filterOptions, setFilterOptions] = useState({
    minPrice: 0,
    maxPrice: 1000,
    minSeats: 1,
    maxSeats: 10,
  });

  useEffect(() => {
    const fetchVehicles = async () => {
      try {
        const step2Data = JSON.parse(localStorage.getItem('step2Data'));
        const token = localStorage.getItem('token');
        const response = await axios.get(`https://calm-retreat-90846-cd036e8a822e.herokuapp.com/https://api.rentsyst.com/v2/booking/search`, {
          params: {
            date_from: step2Data.pickupDateTime,
            date_to: step2Data.returnDateTime,
            pickup_location: step2Data.selectedLocation,
            return_location: step2Data.selectedLocation,
          },
          headers: {
            Authorization: `Bearer ${token}`
          }
        });

        console.log('API Response:', response.data); // Check the response data
        setVehicles(response.data.data); // Populate vehicles array
        setLoading(false);
      } catch (error) {
        console.error('Error fetching vehicles:', error);
        setError('Failed to fetch vehicles');
        setLoading(false);
      }
    };

    fetchVehicles();
  }, []);

  useEffect(() => {
    console.log('Vehicles:', vehicles); // Check the vehicles array
  }, [vehicles]);

  return (
    <div className="step2">
      <h2>Available Vehicles</h2>
      <div className="vehicle-catalog">
        {loading && <p>Loading...</p>}
        {error && <p>{error}</p>}
        {!loading && vehicles && vehicles.length === 0 && <p>No vehicles available</p>}
        {/* Render vehicles here */}
      </div>
    </div>
  );
};

export default Step2;
