import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

const Step2 = () => {
  const defaultFilters = {
    tripPrice: { min: '0', max: '1000' },
    vehicleType: 'All',
    fuelType: 'All',
    transmission: 'All'
  };

  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [vehicles, setVehicles] = useState([]);
  const [filter, setFilter] = useState('');
  const [filters, setFilters] = useState(defaultFilters);
  const [cheapestPrice, setCheapestPrice] = useState(0);
  const [mostExpensivePrice, setMostExpensivePrice] = useState(1000);
  const [currentPrice, setCurrentPrice] = useState(0);

  useEffect(() => {
    const fetchVehicles = async () => {
      try {
        const step2Data = JSON.parse(localStorage.getItem('step2Data'));
        const token = localStorage.getItem('token');

        const { pickupDateTime, returnDateTime, selectedLocation } = step2Data;

        const fromDate = encodeURIComponent(pickupDateTime);
        const toDate = encodeURIComponent(returnDateTime);

        const url = `https://calm-retreat-90846-cd036e8a822e.herokuapp.com/https://api.rentsyst.com/v2/booking/search?date_from=${fromDate}&date_to=${toDate}&pickup_location=${selectedLocation}&return_location=${selectedLocation}`;

        const response = await axios.get(url, {
          headers: {
            Authorization: `Bearer ${token}`
          }
        });

        if (!response.data || !response.data.vehicles) {
          throw new Error('Invalid response data format');
        }

        const parsedVehicles = response.data.vehicles.map(vehicle => {
          const {
            id,
            mark,
            thumbnail,
            price,
            type,
            fuel,
            transmission
          } = vehicle;

          return {
            id,
            mark,
            thumbnail,
            price: typeof price === 'string' ? price : String(price),
            type,
            fuel,
            transmission
          };
        });

        let cheapest = Number.MAX_VALUE;
        let mostExpensive = 0;

        parsedVehicles.forEach(vehicle => {
          const price = parseFloat(vehicle.price.substring(1));
          const pickupDate = new Date(pickupDateTime);
          const returnDate = new Date(returnDateTime);
          const timeDifference = returnDate.getTime() - pickupDate.getTime();
          const hoursDifference = timeDifference / (1000 * 3600);
          const blocksOf24Hours = Math.ceil(hoursDifference / 24);
          const totalTripPrice = price * blocksOf24Hours;

          if (totalTripPrice < cheapest) {
            cheapest = totalTripPrice;
          }

          if (totalTripPrice > mostExpensive) {
            mostExpensive = totalTripPrice;
          }
        });

        setCheapestPrice(cheapest);
        setMostExpensivePrice(mostExpensive);

        let filteredVehicles = parsedVehicles.filter(vehicle => {
          const brand = vehicle.mark ? vehicle.mark.toLowerCase() : '';
          const filterLowerCase = filter.toLowerCase();
          return brand.includes(filterLowerCase);
        });

        if (filters.vehicleType !== 'All') {
          filteredVehicles = filteredVehicles.filter(vehicle => vehicle.type === filters.vehicleType);
        }

        if (filters.fuelType !== 'All') {
          filteredVehicles = filteredVehicles.filter(vehicle => vehicle.fuel === filters.fuelType);
        }

        if (filters.transmission !== 'All') {
          filteredVehicles = filteredVehicles.filter(vehicle => vehicle.transmission === filters.transmission);
        }

        if (filters.tripPrice.min !== '' && filters.tripPrice.max !== '') {
          filteredVehicles = filteredVehicles.filter(vehicle => {
            const price = parseFloat(vehicle.price.substring(1));
            const pickupDate = new Date(pickupDateTime);
            const returnDate = new Date(returnDateTime);
            const timeDifference = returnDate.getTime() - pickupDate.getTime();
            const hoursDifference = timeDifference / (1000 * 3600);
            const blocksOf24Hours = Math.ceil(hoursDifference / 24);
            const totalTripPrice = price * blocksOf24Hours;
            return totalTripPrice >= parseFloat(filters.tripPrice.min) && totalTripPrice <= parseFloat(filters.tripPrice.max);
          });
        }

        setVehicles(filteredVehicles);
      } catch (error) {
        console.error('Error fetching vehicles:', error);
        setError('Failed to fetch vehicles: ' + error.message);
      } finally {
        setLoading(false);
      }
    };

    fetchVehicles();
  }, [filter, filters]);

  const resetFilters = () => {
    setFilter(''); // Reset filter
    setFilters(defaultFilters);
  };

  const reserveNow = (vehicleId) => {
    console.log(`Reserving vehicle with ID: ${vehicleId}`);
  };

  const handleTripPriceChange = (e) => {
    const value = e.target.value;
    setCurrentPrice(value);
    setFilters({ ...filters, tripPrice: { ...filters.tripPrice, max: value } });
  };

  return (
    <div>
      <div className="header-bar"></div>
      <div className="catalog-container" style={{ display: 'flex', justifyContent: 'space-between' }}>
        <img src="/logo.png" alt="Logo" className="logo" />
        <div className="vehicle-list" style={{ width: 'calc(100% - 320px)', height: '100%', overflowY: 'auto', overflowX: 'hidden', msOverflowStyle: 'none', scrollbarWidth: 'none' }}>
          {loading && <p>Loading Available Vehicles...</p>}
          {error && (
            <div>
              <p>{error}</p>
              <button onClick={() => window.location.reload()}>Retry</button>
            </div>
          )}
          {!loading && !error && vehicles.length === 0 && <p>No vehicles available</p>}
          {!loading && !error && vehicles.length > 0 && (
            <div className="vehicle-grid">
              {vehicles.map(({ id, mark, thumbnail, price }) => (
                <div key={id} className="vehicle-item" style={{ maxWidth: '300px', maxHeight: '400px', overflow: 'hidden' }}>
                  <img src={thumbnail} alt={mark} className="vehicle-thumbnail" style={{ width: '100%', height: 'auto', objectFit: 'cover' }} />
                  <h3>{mark}</h3>
                  <p>Daily Price:  ${price}</p> {/* Display daily price */}
                  <button className="reserve-button" onClick={() => reserveNow(id)}>Reserve Now</button>
                </div>
              ))}
            </div>
          )}
        </div>
        <div className="sidebar">
          <div className="filter-section">
            <h3>Filter by brand or model</h3>
            <input
              type="text"
              placeholder="Enter brand or model"
              value={filter}
              onChange={e => setFilter(e.target.value)}
              className="filter-input"
            />
          </div>
          <div className="filter-section">
            <h3>Vehicle Type</h3>
            <select
              value={filters.vehicleType}
              onChange={e => setFilters({ ...filters, vehicleType: e.target.value })}
            >
              <option value="All">All</option>
              <option value="Sedan">Sedan</option>
              <option value="SUV">SUV</option>
              <option value="Truck">Truck</option>
            </select>
          </div>
          <div className="filter-section">
            <h3>Fuel Type</h3>
            <select
              value={filters.fuelType}
              onChange={e => setFilters({ ...filters, fuelType: e.target.value })}
            >
              <option value="All">All</option>
              <option value="Gasoline">Gasoline</option>
              <option value="Diesel">Diesel</option>
            </select>
          </div>
          <div className="filter-section">
            <h3>Transmission</h3>
            <select
              value={filters.transmission}
              onChange={e => setFilters({ ...filters, transmission: e.target.value })}
            >
              <option value="All">All</option>
              <option value="Automatic">Automatic</option>
              <option value="Manual">Manual</option>
            </select>
          </div>
          <div className="filter-section">
            <h3>Trip Price</h3>
            <input
              type="range"
              min={cheapestPrice}
              max={mostExpensivePrice}
              value={currentPrice}
              onChange={handleTripPriceChange}
              step="10"
            />
            <div className="price-label">
              <span>${cheapestPrice}</span>
              <span>${mostExpensivePrice}</span>
            </div>
          </div>
          <div className="reset-filter">
            <button onClick={resetFilters}>Reset Filters</button>
          </div>
        </div>
      </div>
