import React, { useState, useEffect } from 'react';
import { useHistory } from 'react-router-dom';
import axios from 'axios';
import PlacesAutocomplete, { geocodeByAddress, getLatLng } from 'react-places-autocomplete';
import DatePicker from 'react-datepicker';
import './App.css';
import 'react-datepicker/dist/react-datepicker.css';

const Step1 = () => {
  const [step, setStep] = useState(1);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [selectedLocation, setSelectedLocation] = useState('');
  const [pickupDateTime, setPickupDateTime] = useState(null);
  const [returnDateTime, setReturnDateTime] = useState(null);
  const [deliverToMe, setDeliverToMe] = useState(false);
  const [customAddress, setCustomAddress] = useState('');
  const [locations, setLocations] = useState([]);
  const history = useHistory();

  useEffect(() => {
    const fetchData = async () => {
      try {
        const tokenResponse = await axios.post('https://calm-retreat-90846-cd036e8a822e.herokuapp.com/https://api.rentsyst.com/oauth2/token', {
          client_id: '3vN90WwtPO_FpaJXpC4YlG5IWdJE4GZG',
          client_secret: 'Jfjrmns8K25aDY_zQdz9Q6KolwgITUxp',
          grant_type: 'client_credentials'
        });
        const token = tokenResponse.data.access_token;
        localStorage.setItem('token', token); // Store token in localStorage

        const locationsResponse = await axios.get('https://calm-retreat-90846-cd036e8a822e.herokuapp.com/https://api.rentsyst.com/v2/company/settings', {
          headers: {
            Authorization: `Bearer ${token}`
          }
        });
        const data = locationsResponse.data.locations;
        setLocations(data);

      } catch (error) {
        console.error('Error fetching data:', error);
        setError('Failed to fetch data');
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  const handleLocationChange = (e) => {
    const value = e.target.value;
    setSelectedLocation(value);
    if (value === 'deliver_to_me') {
      setDeliverToMe(true);
    } else {
      // Find the selected location object
      const selectedLocationObj = locations.find(location => location.id === value);
      // Store the selected location's name in localStorage
      if (selectedLocationObj) {
        localStorage.setItem('selectedLocationName', selectedLocationObj.name);
      }
      setDeliverToMe(false);
      setStep(2);
    }
  };

  const handleSelect = async (value) => {
    setSelectedLocation('deliver_to_me');
    try {
      const results = await geocodeByAddress(value);
      await getLatLng(results[0]);
    } catch (error) {
      console.error('Error selecting address:', error);
      setError(error.message);
    }
  };

  const handlePickupChange = (date) => {
    setPickupDateTime(date);
  };

  const handleReturnChange = (date) => {
    setReturnDateTime(date);
  };

  const handleSubmitCustomAddress = () => {
    // Assuming customAddress holds the address input by the user
    localStorage.setItem('selectedLocationName', customAddress);
    setStep(2);
  };


  const handlePickupConfirmation = () => {
    setStep(3);
    // Store the pickup location's name in localStorage
    const selectedLocationObj = locations.find(location => location.id === selectedLocation);
    if (selectedLocationObj) {
      localStorage.setItem('pickupLocationName', selectedLocationObj.name);
    }
  };

  const handleReturnConfirmation = () => {
    setStep(4);
    // Store the return location's name in localStorage
    const selectedLocationObj = locations.find(location => location.id === selectedLocation);
    if (selectedLocationObj) {
      localStorage.setItem('returnLocationName', selectedLocationObj.name);
    }
  };

  const handleViewVehicles = () => {
    // Navigate to Step2
    history.push('/step2');
  };



  return (
    <div className="app">
      {loading && <LoadingScreen />}
      {!loading && (
        <>
          <img src="/logo.png" alt="G RIDES Logo" className="logo" />
          <h1>Your destination awaits…</h1>
          {step === 1 && (
            <div className="step">
              <label htmlFor="location">Pickup and Return Location:</label>
              <select id="location" value={selectedLocation} onChange={handleLocationChange} required>
                <option value="">Select Location</option>
                {locations.map(location => (
                  <option key={location.id} value={location.id}>{location.name}</option>
                ))}
                <option value="deliver_to_me">Deliver to me</option>
              </select>
              {deliverToMe && (
                <div className="form-group">
                  <label htmlFor="customAddress">Enter Delivery Address (charges may apply):</label>
                  <div className="custom-address-input">
                    <PlacesAutocomplete
                      value={customAddress}
                      onChange={setCustomAddress}
                      onSelect={handleSelect}
                      searchOptions={{
                        types: ['address'],
                        apiKey: 'YOUR_GOOGLE_API_KEY',
                        language: 'en',
                      }}
                    >
                      {({ getInputProps, suggestions, getSuggestionItemProps, loading }) => (
                        <div className="autocomplete-container">
                          <input
                            {...getInputProps({
                              placeholder: 'Search for your address...',
                              className: 'location-search-input',
                            })}
                          />
                          <div className="autocomplete-dropdown-container">
                            {loading && <div>Loading...</div>}
                            {suggestions.map((suggestion) => (
                              <div
                                {...getSuggestionItemProps(suggestion, {
                                  className: suggestion.active ? 'suggestion-item--active' : 'suggestion-item',
                                })}
                              >
                                {suggestion.description}
                              </div>
                            ))}
                          </div>
                        </div>
                      )}
                    </PlacesAutocomplete>
                    <button onClick={handleSubmitCustomAddress}>Submit Address</button>
                  </div>
                </div>
              )}
            </div>
          )}
          {step === 2 && (
            <div className="step">
              <label htmlFor="pickupDate">Pickup Date and Time:</label>
              <div className="date-time-picker-container">
                <DatePicker
                  id="pickupDate"
                  selected={pickupDateTime}
                  onChange={handlePickupChange}
                  showTimeSelect
                  dateFormat="MMMM d, yyyy h:mm aa"
                  required
                  calendarClassName="custom-calendar"
                />
                <button className="confirm-button" onClick={handlePickupConfirmation}>Confirm</button>
              </div>
            </div>
          )}
          {step === 3 && (
            <div className="step">
              <label htmlFor="returnDate">Return Date and Time:</label>
              <div className="date-time-picker-container">
                <DatePicker
                  id="returnDate"
                  selected={returnDateTime}
                  onChange={handleReturnChange}
                  showTimeSelect
                  dateFormat="MMMM d, yyyy h:mm aa"
                  required
                  calendarClassName="custom-calendar"
                />
                <button className="confirm-button" onClick={handleReturnConfirmation}>Confirm</button>
              </div>
              <div style={{ color: 'white', fontSize: '16px', marginTop: '10px' }}>*Please enter Return Time</div>
            </div>
          )}
          {step === 4 && (
            <div className="step">
              <button className="submit-button" onClick={handleViewVehicles}>View Vehicles</button>
            </div>
          )}
          {error && <div className="error-message">{error}</div>}
        </>
      )}
    </div>
  );
};



const LoadingScreen = () => (
  <div className="loading-screen">
    <img src="/logo.png" alt="G RIDES Logo" className="logo" />
    <div className="loading-bar-container">
      <div className="loading-bar" style={{ width: `90%` }}></div>
    </div>
  </div>
);

export default Step1;
