import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useLocation } from 'react-router-dom'; // Import useLocation
import './App.css';
import moment from 'moment';


// Define defaultFilters before using it
const defaultFilters = {
  tripPrice: { min: 0, max: 1000 },
  vehicleType: 'All',
  fuelType: 'All',
  transmission: 'All',
};
const formattedDate = safeDateFormat(dateFrom);

const formatDate = (dateString) => {
  const options = { year: 'numeric', month: 'long', day: 'numeric' };
  return new Intl.DateTimeFormat('en-US', options).format(new Date(dateString));
};


function validateAndFormatDate(dateString) {
  const date = new Date(dateString);
  if (isNaN(date.getTime())) { // Check if the date is valid
    console.error('Invalid date:', dateString);
    return 'Invalid Date';
  }

  const options = { year: 'numeric', month: '2-digit', day: '2-digit', hour: '2-digit', minute: '2-digit', second: '2-digit', hour12: false };
  return new Intl.DateTimeFormat('en-US', options).format(date);
}

const Step2 = () => {
  const [customAddress, setCustomAddress] = useState('');
  const [pickupLocation, setPickupLocation] = useState('');
  const [returnLocation, setReturnLocation] = useState('');
  const location = useLocation();
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [vehicles, setVehicles] = useState([]);
  const [filter, setFilter] = useState('');
  const [filters, setFilters] = useState(defaultFilters);
  const [currentPrice, setCurrentPrice] = useState(0);
  const [filteredVehicles, setFilteredVehicles] = useState([]); // State to hold filtered vehicles
  const [finalReturnLocation, setFinalReturnLocation] = useState('');
  const [dateFrom, setDateFrom] = useState('');
  const [dateTo, setDateTo] = useState('');
  const [reservationDuration, setReservationDuration] = useState('');

  function formatDateTime(date) {
    const pad = (number) => (number < 10 ? '0' + number : number);

    const year = date.getFullYear();
    const month = pad(date.getMonth() + 1); // getMonth() returns 0-11
    const day = pad(date.getDate());
    const hours = pad(date.getHours());
    const minutes = pad(date.getMinutes());
    const seconds = pad(date.getSeconds());

    return `${year}-${month}-${day} ${hours}:${minutes}:${seconds}`;
  }


  useEffect(() => {
    // Assuming you have stored these values in local storage with corresponding keys
    const storedReturnLocation = localStorage.getItem('returnLocation') || 'Not specified';
    const storedDateFrom = localStorage.getItem('dateFrom') || 'Not specified';
    const storedDateTo = localStorage.getItem('dateTo') || 'Not specified';
    // Assuming you store reservationDuration in local storage, if not, you'll need to calculate or set it differently
    const storedReservationDuration = localStorage.getItem('reservationDuration') || 'Not specified';

    // Set state with values retrieved from local storage
    setFinalReturnLocation(storedReturnLocation);
    setDateFrom(storedDateFrom);
    setDateTo(storedDateTo);

    // Calculate reservation duration
    if (storedDateFrom && storedDateTo) {
      const dateFrom = new Date(storedDateFrom);
      const dateTo = new Date(storedDateTo);
      const differenceInTime = dateTo.getTime() - dateFrom.getTime();
      const differenceInDays = Math.ceil(differenceInTime / (1000 * 3600 * 24)); // Convert time difference to days and round up

      setReservationDuration(differenceInDays);
    }
  }, [dateFrom, dateTo]);

  const resetFilters = () => {
    setFilter('');
    setFilters(defaultFilters);
    setCurrentPrice(0);
  };


  useEffect(() => {
    const fetchVehicles = async () => {
      setLoading(true);
      try {
        const token = localStorage.getItem('token'); // Assuming you store the token in localStorage
