import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useLocation } from 'react-router-dom'; // Import useLocation
import './App.css';
import moment from 'moment';



// Define defaultFilters before using it
const defaultFilters = {
  tripPrice: { min: 0, max: 1000 },
  vehicleType: 'All',
  fuelType: 'All',
  transmission: 'All',
};

function safeDateFormat(dateInput) {
  if (!dateInput) {
    console.error('Date input is undefined, null, or empty');
    return 'Invalid Date';
  }

  const date = new Date(dateInput);

  // Check if the date is valid
  if (isNaN(date.getTime())) {
    console.error('Invalid date:', dateInput);
    return 'Invalid Date';
  }

  // If the date is valid, format it
  const options = { year: 'numeric', month: 'long', day: 'numeric' };
  return new Intl.DateTimeFormat('en-US', options).format(date);
}

const formatDate = (dateString) => {
  if (!dateString) return 'Invalid Date'; // Handle null, undefined, or empty string more safely

  const date = new Date(dateString);
  if (isNaN(date.getTime())) {
    return 'Invalid Date'; // Return 'Invalid Date' if the date is not valid
  }
  const options = { year: 'numeric', month: 'long', day: 'numeric' };
  return new Intl.DateTimeFormat('en-US', options).format(date);
};


function validateAndFormatDate(dateString) {
  if (!dateString) return ''; // Handle null, undefined, or empty string

  const date = moment(dateString);
  if (!date.isValid()) {
    console.error('Invalid date:', dateString);
    return ''; // Return an empty string or handle as needed
  }

  return date.format('YYYY-MM-DD HH:mm:ss');
}

const Step2 = () => {
  const [customAddress, setCustomAddress] = useState('');
  const [pickupLocation, setPickupLocation] = useState('');
  const [returnLocation, setReturnLocation] = useState('');
  const location = useLocation();
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [vehicles, setVehicles] = useState([]);
  const [filter, setFilter] = useState('');
  const [filters, setFilters] = useState(defaultFilters);
  const [currentPrice, setCurrentPrice] = useState(0);
  const [filteredVehicles, setFilteredVehicles] = useState([]); // State to hold filtered vehicles
  const [finalReturnLocation, setFinalReturnLocation] = useState('');
  const [dateFrom, setDateFrom] = useState('');
  const [dateTo, setDateTo] = useState('');
  const [reservationDuration, setReservationDuration] = useState('');

  function generateDescription(vehicle) {
    const descriptors = [
      "Experience ultimate driving comfort with",
      "Elevate your journey with the sophisticated",
      "Turn heads in the sleek and stylish",
      "Embrace the road ahead with unparalleled performance of",
      "Discover the perfect blend of luxury and efficiency in",
    ];
    const uniqueIdentifier = `${vehicle.brand} ${vehicle.mark} ${vehicle.year}`;
    const randomIndex = Math.floor(Math.random() * descriptors.length);
    const baseDescription = descriptors[randomIndex];
    const uniquenessFactor = vehicle.color ? `in its stunning ${vehicle.color.title} color` : `with its unique charm`;

    return `${baseDescription} the ${uniqueIdentifier} ${uniquenessFactor}.`;
  }

  useEffect(() => {
    const fetchVehicles = async () => {
      setLoading(true);
      try {
        const token = localStorage.getItem('token'); // Assuming you store the token in localStorage

        const storedDateFrom = localStorage.getItem('dateFrom');
        const storedDateTo = localStorage.getItem('dateTo'); // Retrieve dateTo if needed

        const formattedDateFrom = storedDateFrom ? validateAndFormatDate(storedDateFrom) : '';
        const formattedDateTo = storedDateTo ? validateAndFormatDate(storedDateTo) : '';

        const queryParams = new URLSearchParams({
          date_from: formattedDateFrom,
          date_to: formattedDateTo,
        });

        if (customAddress) {
          queryParams.append('custom_address', customAddress);
        } else {
          queryParams.append('pickup_location', pickupLocation);
          queryParams.append('return_location', returnLocation);
        }

        const response = await axios.get(`https://calm-retreat-90846-cd036e8a822e.herokuapp.com/https://api.rentsyst.com/v2/booking/search?${queryParams.toString()}`, {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        const vehicleCatalog = response.data.vehicles.map(vehicle => ({
          id: vehicle.id,
          brand: vehicle.brand,
          mark: vehicle.mark,
          year: vehicle.year,
          thumbnail: vehicle.thumbnail,
          price: vehicle.price,
          totalPrice: vehicle.price * reservationDuration,
          description: generateDescription(vehicle),
          topFeatures: vehicle.top_features
        }));
        setVehicles(vehicleCatalog); // Adjust according to your API response structure
      } catch (error) {
        console.error('Failed to fetch vehicles:', error);
        setError('Failed to fetch available vehicles. Please try again later.');
      } finally {
        setLoading(false);
      }
    };

    fetchVehicles();
  }, [pickupLocation, returnLocation, customAddress]); // Removed dateFrom and dateTo from dependencies array

  const resetFilters = () => {
    setFilter('');
    setFilters(defaultFilters);
    setCurrentPrice(0);
  };

  useEffect(() => {
    filterVehiclesByPrice(vehicles);
  }, [currentPrice, reservationDuration, vehicles]);

  const handleTripPriceChange = (event) => {
    setCurrentPrice(event.target.value);
  };

  const reserveNow = (vehicleId) => {
    console.log(`Reserve vehicle with ID: ${vehicleId}`);
  };

  useEffect(() => {
    const fetchVehicles = async () => {
      try {
        const response = await axios.get('API_ENDPOINT_URL');
        const vehicleCatalog = response.data.vehicles.map(vehicle => ({
          name: `${vehicle.brand} ${vehicle.mark}`,
          year: vehicle.year,
          seats: vehicle.number_seats,
          doors: vehicle.number_doors,
          largeBags: vehicle.large_bags,
          smallBags: vehicle.small_bags,
          odometer: vehicle.odometer,
          group: vehicle.group,
          color: vehicle.color.title,
          type: vehicle.type,
          bodyType: vehicle.body_type,
          price: vehicle.price,
          currency: vehicle.currency,
          consumption: vehicle.consumption,
          fuel: vehicle.fuel,
          volumeTank: vehicle.volume_tank,
          volumeEngine: vehicle.volume_engine,
          transmission: vehicle.transmission,
          wheelDrive: vehicle.wheel_drive,
          registrationNumber: vehicle.registration_number,
          totalPrice: vehicle.price * reservationDuration,
          description: generateDescription(vehicle),
          countDays: vehicle.count_days,
          refill: vehicle.refill,
          mileageLimit: vehicle.mileage_limit,
          thumbnail: vehicle.thumbnail,
          photos: vehicle.photos,
          options: vehicle.options.map(option => option.name),
        }));
        setVehicles(vehicleCatalog);
      } catch (error) {
        console.error('Failed to fetch vehicles:', error);
      }
    };

    fetchVehicles();
  }, []);

  return (
    <div>
      <div className="top-bar" style={{ position: 'relative', paddingTop: '20px' }}>
        <img src="/logo.png" alt="Logo" className="logo" style={{ position: 'absolute', top: '50px', left: '10px', marginTop: '0' }} />
        <div className="reservation-details" style={{ marginLeft: '120px', marginTop: '20px', display: 'flex', alignItems: 'center', color: '#eac831' }}>
          <p>Location: <span className="detail-value">{finalReturnLocation}</span></p>
          <p>Date From: <span className="detail-value">{safeDateFormat(dateFrom)}</span></p>
          <p>Date To: <span className="detail-value">{safeDateFormat(dateTo)}</span></p>
          <p>Duration: <span className="detail-value">{reservationDuration} days</span></p>
        </div>
      </div>
      <div className="catalog-container" style={{ display: 'flex', justifyContent: 'space-between', paddingTop: '20px' }}>
        <div className="vehicle-list" style={{ zIndex: 9999, width: 'calc(100% - 320px)', height: '100%', overflowY: 'auto', overflowX: 'hidden', msOverflowStyle: 'none', scrollbarWidth: 'none', marginTop: '20px' }}>
          {loading && <p>Loading available vehicles...</p>}
          {error && <p>{error}</p>}
          {!loading && !error && vehicles.length > 0 && (
            <div className="vehicle-grid">
              {vehicles.map((vehicle) => {
                return (
                  <div key={vehicle.id} className="vehicle-item">
                    <img src={vehicle.thumbnail} alt={`${vehicle.brand} ${vehicle.mark}`} className="vehicle-thumbnail" />
                    <div className="vehicle-info">
                      <h3>{`${vehicle.brand} ${vehicle.mark} ${vehicle.year}`}</h3>
                      <p className="vehicle-description">{vehicle.description}</p>
                      <p className="price-info">Daily Price: ${vehicle.price} | Trip Price: ${vehicle.totalPrice}</p>
                      <button className="reserve-button" onClick={() => reserveNow(vehicle.id)}>Reserve Now</button>
                    </div>
                  </div>
                );
              })}
            </div>
          )}
        </div>
        <div className="sidebar" style={{ marginTop: '20px' }}>
          <div className="filter-section">
            <h3>Filter by brand or model</h3>
            <input
              type="text"
              placeholder="Enter brand or model"
              value={filter}
              onChange={e => setFilter(e.target.value)}
              className="filter-input"
            />
          </div>
          <div className="filter-section">
            <h3>Vehicle Type</h3>
            <select
              value={filters.vehicleType}
              onChange={e => setFilters({ ...filters, vehicleType: e.target.value })}
            >
              <option value="All">All</option>
              <option value="Sedan">Sedan</option>
              <option value="SUV">SUV</option>
              <option value="Truck">Truck</option>
            </select>
          </div>
          <div className="filter-section">
            <h3>Fuel Type</h3>
            <select
              value={filters.fuelType}
              onChange={e => setFilters({ ...filters, fuelType: e.target.value })}
            >
              <option value="All">All</option>
              <option value="Gasoline">Gasoline</option>
              <option value="Diesel">Diesel</option>
            </select>
          </div>
          <div className="filter-section">
            <h3>Transmission</h3>
            <select
              value={filters.transmission}
              onChange={e => setFilters({ ...filters, transmission: e.target.value })}
            >
              <option value="All">All</option>
              <option value="Automatic">Automatic</option>
              <option value="Manual">Manual</option>
            </select>
          </div>
          <div className="filter-section">
            <h3>Max Trip Price</h3>
            <input
              type="range"
              min="0"
              max="10000"
              value={currentPrice}
              onChange={handleTripPriceChange}
              step="10"
            />
            <div className="price-label">
              <span>${currentPrice}</span>
            </div>
          </div>
          <div className="reset-filter">
            <button onClick={resetFilters}>Reset Filters</button>
          </div>
        </div>
      </div>
    </div>
  );
};

